import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import classnames from "classnames";
import { addProfileTask } from "../../../actions/profileBacklogActions";
import PropTypes from "prop-types";
import NumericInput from "react-numeric-input";

class AddProfileTask extends Component {
  constructor(props) {
    super(props);
    const { id } = this.props.match.params;

    this.state = {
      summary: "",
      acceptanceCriteria: "",
      status: "",
      priority: 0,
      dueDate: "",
      profileIdentifier: id,
      totalYears: 0,
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  // on change
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  //on submit
  onSubmit(e) {
    e.preventDefault();

    const newTask = {
      summary: this.state.summary,
      acceptanceCriteria: this.state.acceptanceCriteria,
      status: this.state.status,
      totalYears: this.state.totalYears,
      priority: this.state.priority,
      dueDate: this.state.dueDate
    };

    this.props.addProfileTask(
      this.state.profileIdentifier,
      newTask,
      this.props.history
    );
  }

  onTextChanged(e) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ totalYears: e });
    // }
    if (e) {
      if (!/^[0-9]+$/.test(e.toString())) {
        this.setState({ totalYears: "" });
      }
    }
  }

  render() {
    const { id } = this.props.match.params;
    const { errors } = this.state;
    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link to={`/editcv/${id}`} className="btn btn-light">
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">Add Skill</h4>
              <p className="lead text-center">
                Profile Identifier: {this.state.profileIdentifier}{" "}
              </p>
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.summary
                    })}
                    name="summary"
                    placeholder="Example: Object Oriented Analysis"
                    value={this.state.summary}
                    onChange={this.onChange}
                  />
                  {errors.summary && (
                    <div className="invalid-feedback">{errors.summary}</div>
                  )}
                </div>

                <div className="form-group">
                  <textarea
                    className="form-control form-control-lg"
                    placeholder="Acceptance Criteria"
                    name="acceptanceCriteria"
                    value={this.state.acceptanceCriteria}
                    onChange={this.onChange}
                  />
                </div>
                <h6>Total of Years:</h6>
                <NumericInput
                  mobile
                  min={0}
                  max={50}
                  className="form-control"
                  name="totalYears"
                  onChange={e => this.onTextChanged(e)}
                  value={this.state.totalYears}
                />
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

AddProfileTask.propTypes = {
  addProfileTask: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { addProfileTask }
)(AddProfileTask);
