import axios from "axios";
import {
  GET_ERRORS,
  GET_PROFILE_BACKLOG,
  GET_PROFILE_TASK,
  DELETE_PROFILE_TASK
} from "./types";

//Fix bug with priority in Spring Boot Server, needs to check null first
export const addProfileTask = (
  profile_backlog_id,
  profile_task,
  history
) => async dispatch => {
  try {
    await axios.post(`/api/profileBacklog/${profile_backlog_id}`, profile_task);
    alert("Is About to do history Push", profile_backlog_id);
    history.push(`/CvBoard/${profile_backlog_id}`);
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfileBacklog = profile_backlog_id => async dispatch => {
  try {
    const res = await axios.get(`/api/profileBacklog/${profile_backlog_id}`);
    dispatch({
      type: GET_PROFILE_BACKLOG,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfileTask = (
  profile_backlog_id,
  pt_id,
  history
) => async dispatch => {
  try {
    const res = await axios.get(
      `/api/profileBacklog/${profile_backlog_id}/${pt_id}`
    );
    dispatch({
      type: GET_PROFILE_TASK,
      payload: res.data
    });
  } catch (err) {
    alert("Is About to do history Push", err);
    //history.push("/dashboard");
  }
};

export const updateProfileTask = (
  profile_backlog_id,
  pt_id,
  project_task,
  history
) => async dispatch => {
  try {
    await axios.patch(
      `/api/profileBacklog/${profile_backlog_id}/${pt_id}`,
      project_task
    );
    alert("Is About to do history Push", updateProfileTask);
    history.push(`/CVBoard/${profile_backlog_id}`);
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const deleteProfileTask = (
  profile_backlog_id,
  pt_id
) => async dispatch => {
  if (
    window.confirm(
      `You are deleting project task ${pt_id}, this action cannot be undone`
    )
  ) {
    await axios.delete(`/api/profileBacklog/${backlog_id}/${pt_id}`);
    dispatch({
      type: DELETE_PROFILE_TASK,
      payload: pt_id
    });
  }
};
