import React, { Component } from "react";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import NumericInput from "react-numeric-input";
import {
  addProfileTask,
  updateProfileTask
} from "../../../../actions/profileBacklogActions";

class ClientProjectForm extends Component {
  constructor() {
    super();

    this.state = {
      id: "",
      title: "",
      profileSequence: "",
      profileIdentifier: "",
      start_date: "",
      end_date: "",
      months: "",
      position: "",
      company: "",
      generalContext: "",
      responsabilities: "",
      techinicalEnviroment: "",
      create_At: "",
      profile: {},
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onChangeText = this.onChangeText.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    console.log(this.state);
    console.log(this.props);
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeText(value) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ educYear: e });
    // }
    if (value) {
      if (!/^[0-9]+$/.test(value.toString())) {
        this.setState({ educYear: "" });
      } else {
        this.setState({ educYear: value.toString() });
      }
    }
  }

  onSubmit(e) {
    e.preventDefault();
    if (this.props.typeForm === "Add") {
      // const newTask = {
      //   educYear: this.state.educYear,
      //   educAwarded: this.state.educAwarded,
      //   educOrganization: this.state.educOrganization
      // };
      const newTask = {
        ...this.props,
        //profileStaged: action.payload
        profile: {
          ...this.props.profile,
          start_date: this.state.start_date,
          end_date: this.state.end_date,
          months: this.state.months,
          position: this.state.position,
          company: this.state.company,
          generalContext: this.state.generalContext,
          responsabilities: this.state.responsabilities,
          techinicalEnviroment: this.state.techinicalEnviroment,
          taskType: this.props.taskType
        }
      };

      this.props.addProfileTask(
        this.props.profileIdentifier,
        newTask.profile,
        this.props.history
      );
    } else {
      const UpdateProfileTask = {
        id: this.state.id,
        start_date: this.state.start_date,
        end_date: this.state.end_date,
        months: this.state.months,
        position: this.state.position,
        company: this.state.company,
        generalContext: this.state.generalContext,
        responsabilities: this.state.responsabilities,
        techinicalEnviroment: this.state.techinicalEnviroment
      };

      // console.log(UpdateProfileTask);
      this.props.updateProfileTask(
        this.state.profileIdentifier,
        this.state.profileSequence,
        UpdateProfileTask,
        this.props.history
      );
    }
  }

  render() {
    const { errors, typeForm } = this.props;

    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/editcv/${this.props.profileIdentifier}`}
                className="btn btn-light"
              >
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">
                {typeForm} Client/Project
              </h4>
              <div className="lead text-center">
                <hr />
                {this.props.profile.profileSequence && (
                  <span>
                    Client/Project ID: {this.props.profile.profileSequence}{" "}
                  </span>
                )}
                {errors.profileNotFound && (
                  <div
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {"Please Check the fields above:"}
                    <hr />
                    {errors.profileNotFound}
                  </div>
                )}
              </div>
              <form onSubmit={this.onSubmit}>
                <div className="container">
                  <div className="row">
                    <div className="form-group col">
                      <h6>Start Date</h6>
                      <input
                        type="date"
                        className={classnames("form-control form-control-lg", {
                          "is-invalid": errors.position
                        })}
                        name="start_date"
                        value={this.state.start_date}
                        onChange={this.onChange}
                      />
                    </div>
                    <div className="form-group col">
                      <h6>End Date</h6>
                      <input
                        type="date"
                        className={classnames("form-control form-control-lg", {
                          "is-invalid": errors.position
                        })}
                        name="start_date"
                        value={this.state.start_date}
                        onChange={this.onChange}
                      />
                    </div>
                  </div>
                </div>

                <h6>Position:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.position
                    })}
                    placeholder="Position"
                    name="position"
                    value={this.state.position}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.position && (
                    <div className="invalid-feedback">{errors.position}</div>
                  )}
                </div>

                <h6>Company:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.company
                    })}
                    placeholder="Company"
                    name="company"
                    value={this.state.company}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.company && (
                    <div className="invalid-feedback">{errors.company}</div>
                  )}
                </div>

                <h6>General Context:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.generalContext
                    })}
                    placeholder="General Context"
                    name="generalContext"
                    value={this.state.generalContext}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.generalContext && (
                    <div className="invalid-feedback">
                      {errors.generalContext}
                    </div>
                  )}
                </div>

                <h6>Responsabilities:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.responsabilities
                    })}
                    placeholder="Responsabilities"
                    name="responsabilities"
                    value={this.state.responsabilities}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.responsabilities && (
                    <div className="invalid-feedback">
                      {errors.responsabilities}
                    </div>
                  )}
                </div>

                <h6>Techinical Enviroment:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.techinicalEnviroment
                    })}
                    placeholder="Techinical Enviroment"
                    name="techinicalEnviroment"
                    value={this.state.techinicalEnviroment}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.techinicalEnviroment && (
                    <div className="invalid-feedback">
                      {errors.techinicalEnviroment}
                    </div>
                  )}
                </div>
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ClientProjectForm.propTypes = {
  addProfileTask: PropTypes.func.isRequired,
  updateProfileTask: PropTypes.func.isRequired,
  title: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  title: state.title,
  profile: state.profile.profile,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { addProfileTask, updateProfileTask }
)(ClientProjectForm);
