import { GET_PROFILES, GET_PROFILE, DELETE_PROFILE } from "../actions/types";

const initialState = {
  profiles: [],
  profile: {}
};

const booleanActionPayload = payload => {
  if (payload) {
    return true;
  } else {
    return false;
  }
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_PROFILES:
      return {
        ...state,
        profiles: action.payload
      };

    case GET_PROFILE:
      return {
        ...state,
        validToken: booleanActionPayload(action.payload),
        profile: action.payload
      };

    case DELETE_PROFILE:
      return {
        ...state,
        profiles: state.profiles.filter(
          profile => profile.candidateEmail !== action.payload
        )
      };
    default:
      return state;
  }
}
