import React, { Component } from "react";
import { getProfileByLink, updateCV } from "../../actions/profileActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import requireAuth from "../hocs/requireAuth";
import { setJWTTokenCandidate } from "../securityUtils/setJWTToken";
import jwt_decode from "jwt-decode";

class EditCV extends Component {
  constructor() {
    super();

    this.state = {
      id: "",
      fullName: "",
      candidateEmail: "",
      linkCv: "",
      token: "",
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    const { id, fullName, candidateEmail, linkCv, token } = nextProps.profile;

    this.setState({
      id,
      fullName,
      candidateEmail,
      linkCv,
      token
    });
  }

  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getProfileByLink(id, this.props.history);
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    const updateProfile = {
      id: this.state.id,
      fullName: this.state.fullName,
      candidateEmail: this.state.candidateEmail
    };

    console.log(updateProfile);

    this.props.updateCV(updateProfile, this.props.history);
  }

  render() {
    const { errors } = this.state;

    return (
      <div className="profile">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link to="/profiles" className="btn btn-light">
                Back to Profiles
              </Link>
              <h5 className="display-1 text-center">Edit CV form</h5>
              <hr />
              <div className="text-center">Session expire in 2 days</div>
              <hr />
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.fullName
                    })}
                    placeholder="Full Name"
                    name="fullName"
                    onChange={this.onChange}
                    value={this.state.fullName}
                    autoFocus={true}
                  />
                  {errors.fullName && (
                    <div className="invalid-feedback">{errors.fullName}</div>
                  )}
                </div>
                <div className="form-group">
                  <select
                    className="form-control form-control-lg"
                    name="technoArea"
                    value={this.state.technoArea}
                    onChange={this.onChange}
                  >
                    <option value="">Technologia Area</option>
                    <option value="Software">Software</option>
                    <option value="Engineer">Engineer</option>
                  </select>
                </div>
                <hr />
                Experiences:
                <div className="form-group">
                  <textarea
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.summary
                    })}
                    placeholder="Summary of profile"
                    name="description"
                    value={this.state.summary}
                    onChange={this.onChange}
                  />
                  {errors.summary && (
                    <div className="invalid-feedback">{errors.summary}</div>
                  )}
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.totalYear
                    })}
                    placeholder="Total year of experience"
                    name="totalYear"
                    value={this.state.totalYear}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.totalYear && (
                    <div className="invalid-feedback">{errors.totalYear}</div>
                  )}
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.projectName
                    })}
                    placeholder="Project Name"
                    name="projectName"
                    value={this.state.projectName}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.projectName && (
                    <div className="invalid-feedback">{errors.projectName}</div>
                  )}
                </div>
                <h6>Start in</h6>
                <div className="form-group">
                  <input
                    type="date"
                    className="form-control form-control-lg"
                    name="start_date"
                    value={this.state.start_date}
                    onChange={this.onChange}
                  />
                </div>
                <h6>End in</h6>
                <div className="form-group">
                  <input
                    type="date"
                    className="form-control form-control-lg"
                    name="end_date"
                    value={this.state.end_date}
                    onChange={this.onChange}
                  />
                </div>
                <div className="form-group">
                  <textarea
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.description
                    })}
                    placeholder="Project Description"
                    name="description"
                    value={this.state.description}
                    onChange={this.onChange}
                  />
                  {errors.description && (
                    <div className="invalid-feedback">{errors.description}</div>
                  )}
                </div>
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

EditCV.propTypes = {
  getProfileByLink: PropTypes.func.isRequired,
  updateCV: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile.profile,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { getProfileByLink, updateCV }
)(requireAuth(EditCV));
