import axios from "axios";
import { GET_ERRORS, GET_PROFILES, GET_PROFILE, DELETE_PROFILE } from "./types";
import { setJWTTokenCandidate } from "../securityUtils/setJWTToken";
import jwt_decode from "jwt-decode";

export const createProfile = (profile, history) => async dispatch => {
  try {
    await axios.post("/api/profile", profile);
    history.push("/profiles");
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfiles = () => async dispatch => {
  const res = await axios.get("/api/profile/all");
  dispatch({
    type: GET_PROFILES,
    payload: res.data
  });
};

export const getProfile = (id, history) => async dispatch => {
  try {
    const res = await axios.get(`/api/profile/${id}`);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    history.push("/profiles");
  }
};

export const getProfileByLink = (token, history) => async dispatch => {
  try {
    // set our token in header ***
    setJWTTokenCandidate(token);

    //const res = await axios.post("/api/users/editcv", token);
    const res = await axios.get(
      `https://personal-profiler.herokuapp.com/api/editcv/${token}`
    );

    const jwtToken = localStorage.jwtToken;
    // Only Take Care About Candidate Toke If doenst exist Current User
    if (!jwtToken) {
      // store the token in the localStorage
      localStorage.setItem("jwtTokenCandidate", token);
      // decode token on React
      const decoded_jwtToken = jwt_decode(token);
      console.log("Expire time:", decoded_jwtToken.exp);

      const currentTime = Date.now() / 1000;
      if (decoded_jwtToken.exp < currentTime) {
        window.location.href = "/";
      }
    }

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    console.log(error);
    //history.push("/profiles");
  }
};

export const updateCV = profile => async dispatch => {
  try {
    const res = await axios.post(
      "https://personal-profiler.herokuapp.com/api/editcv",
      profile
    );

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });

    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const deleteProfile = id => async dispatch => {
  if (
    window.confirm(
      "Are you sure? This will delete the project and all the data related to it"
    )
  ) {
    await axios.delete(`/api/profile/${id}`);
    dispatch({
      type: DELETE_PROFILE,
      payload: id
    });
  }
};
