import axios from "axios";
import { GET_ERRORS, GET_PROFILES, GET_PROFILE, DELETE_PROFILE } from "./types";

export const createProfile = (profile, history) => async dispatch => {
  try {
    await axios.post("/api/profile", profile);
    history.push("/profiles");
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfiles = () => async dispatch => {
  const res = await axios.get("/api/profile/all");
  dispatch({
    type: GET_PROFILES,
    payload: res.data
  });
};

export const getProfile = (id, history) => async dispatch => {
  try {
    const res = await axios.get(`/api/profile/${id}`);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    history.push("/profiles");
  }
};

export const getProfileByToken = (token, history) => async dispatch => {
  try {
    const res = await axios.get(`/api/profile/edit_cv/${token}`);
    // store the token in the localStorage
    localStorage.setItem("jwtToken", token);
    // set our token in header ***
    setJWTToken(token);
    // decode token on React
    const decoded = jwt_decode(token);
    console.log(decoded);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    history.push("/profiles");
  }
};

export const deleteProfile = id => async dispatch => {
  if (
    window.confirm(
      "Are you sure? This will delete the project and all the data related to it"
    )
  ) {
    await axios.delete(`/api/profile/${id}`);
    dispatch({
      type: DELETE_PROFILE,
      payload: id
    });
  }
};
