import axios from "axios";
import { GET_ERRORS, GET_PROFILES } from "./types";

export const createNewProfile = (profilecv, history) => async dispatch => {
  try {
    await axios.post("/api/profile", profilecv);
    history.push("/dashboard");
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfiles = () => async dispatch => {
  const res = await axios.get("/api/profile/all");
  dispatch({
    type: GET_PROFILES,
    payload: res.data
  });
};
