import React, { Component } from "react";
import { Link } from "react-router-dom";
//import PropTypes from "prop-types";
import { connect } from "react-redux";

class ProfileItem extends Component {
  render() {
    const { profile } = this.props;
    return (
      <div className="container">
        <div className="card card-body bg-light mb-3">
          <div className="row">
            <div className="col-2">
              <span className="mx-auto">{profile.fullName}</span>
            </div>
            <div className="col-lg-6 col-md-4 col-8">
              <h3>{profile.fullName}</h3>
              <p>{profile.fullName}</p>
            </div>
            <div className="col-md-4 d-none d-lg-block">
              <ul className="list-group">
                <Link to={`/projectBoard/${profile.fullName}`}>
                  <li className="list-group-item board">
                    <i className="fa fa-flag-checkered pr-1"> Generate Link </i>
                  </li>
                </Link>
                <Link to={`/updateProject/${profile.fullName}`}>
                  <li className="list-group-item update">
                    <i className="fa fa-edit pr-1"> Update Profile Info</i>
                  </li>
                </Link>

                <li
                  className="list-group-item delete"
                  onClick={this.onDeleteClick.bind(
                    this,
                    profile.candidateEmail
                  )}
                >
                  <i className="fa fa-minus-circle pr-1"> Delete Profile</i>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ProjectItem.propTypes = {
  deleteProject: PropTypes.func.isRequired
};

export default connect({ deleteProject })(ProfileItem);
