import React, { Component } from "react";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import {
  addProfileTask,
  updateProfileTask
} from "../../../../actions/profileBacklogActions";
import {
  Form,
  FormGroup,
  FormControl,
  ControlLabel,
  Button
} from "react-bootstrap";

class ClientProjectForm extends Component {
  constructor() {
    super();

    this.state = {
      id: "",
      title: "",
      profileSequence: "",
      profileIdentifier: "",
      start_date: "",
      end_date: "",
      months: "",
      position: "",
      company: "",
      generalContext: "",
      responsibilities: [],
      technicalEnviroment: "",
      create_At: "",
      profile_task: {},
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onChangeText = this.onChangeText.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  // componentDidMount() {
  //   //const { profile_backlog_id, pt_id } = this.props.match.params;
  //   this.props.getProfileTask(
  //     this.props.profileIdentifier,
  //     this.props.profileSequence,
  //     this.props.history
  //   );
  // }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    if (
      this.props.typeForm === "Update" &&
      nextProps.profile_task !== this.state.profile_task
    ) {
      if (nextProps.profile_task) {
        this.setState({ profile_task: nextProps.profile_task });
      }

      const {
        id,
        title,
        profileSequence,
        profileIdentifier,
        start_date,
        end_date,
        months,
        position,
        company,
        generalContext,
        responsibilities,
        technicalEnviroment,
        create_At
      } = nextProps.profile_task;

      this.setState({
        id,
        title,
        profileSequence,
        profileIdentifier,
        start_date,
        end_date,
        months,
        position,
        company,
        generalContext,
        responsibilities,
        technicalEnviroment,
        create_At
      });
    }
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeText(value) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ educYear: e });
    // }
    if (value) {
      if (!/^[0-9]+$/.test(value.toString())) {
        this.setState({ educYear: "" });
      } else {
        this.setState({ educYear: value.toString() });
      }
    }
  }

  onSubmit(e) {
    e.preventDefault();
    if (this.props.typeForm === "Add") {
      // const newTask = {
      //   educYear: this.state.educYear,
      //   educAwarded: this.state.educAwarded,
      //   educOrganization: this.state.educOrganization
      // };
      const newTask = {
        ...this.props,
        //profileStaged: action.payload
        profile: {
          ...this.props.profile,
          start_date: this.state.start_date,
          end_date: this.state.end_date,
          months: this.state.months,
          position: this.state.position,
          company: this.state.company,
          generalContext: this.state.generalContext,
          responsibilities: this.state.responsibilities,
          technicalEnviroment: this.state.technicalEnviroment,
          taskType: this.props.taskType
        }
      };

      this.props.addProfileTask(
        this.props.profileIdentifier,
        newTask.profile,
        this.props.history
      );
    } else {
      const UpdateProfileTask = {
        id: this.state.id,
        start_date: this.state.start_date,
        end_date: this.state.end_date,
        months: this.state.months,
        position: this.state.position,
        company: this.state.company,
        generalContext: this.state.generalContext,
        responsibilities: this.state.responsibilities,
        technicalEnviroment: this.state.technicalEnviroment
      };

      // console.log(UpdateProfileTask);
      this.props.updateProfileTask(
        this.state.profileIdentifier,
        this.state.profileSequence,
        UpdateProfileTask,
        this.props.history
      );
    }
  }

  addResponsability() {
    const { responsibilities } = this.state;
    // if (this.props.typeForm === "Update") {
    //   responsibilities = this.props.profile_task.responsibilities;
    // }

    // responsibilities.push({
    //   id: responsibilities.length,
    //   prompt: "",
    //   answer: ""
    // });
    const newResp = { id: responsibilities.length, prompt: "", answer: "" };
    this.setState({
      responsibilities: [
        ...responsibilities,
        { id: responsibilities.length, prompt: "", answer: "" }
      ]
    });
  }

  updateResponsabilityPart(event, index, part) {
    const { responsibilities } = this.state;

    responsibilities[index][part] = event.target.value;

    this.setState({ responsibilities });
  }

  addStack() {
    this.props.addStack(this.state);
  }

  render() {
    const { errors, typeForm, responsibilities } = this.props;

    const boardAlgorithm = (errors, responsibilities, typeForm) => {
      if (
        typeof responsibilities === "undefined" ||
        responsibilities.length < 1
      ) {
        return (
          <div>
            {" "}
            <div className="alert alert-danger text-center" role="alert">
              Please add Responsibilities (max caracteres: 100)
            </div>
          </div>
        );
      } else {
        return (
          <Form>
            <hr />
            <div className="container">
              <div className="row">
                <div className="form-group col">
                  <div className="col-6 text-left">
                    <ControlLabel>Role/function:</ControlLabel>
                  </div>
                </div>
                <div className="form-group col">
                  <div className="col-6 text-left">
                    <ControlLabel>Activities/Tasks:</ControlLabel>
                  </div>
                </div>
              </div>
              {this.state.responsibilities.map((responsability, index) => {
                return (
                  <div key={responsability.id}>
                    <div className="row">
                      <div className="form-group col">
                        <div className="col-6 text-left">
                          <FormControl
                            placeholder="e.g: Functional & technical analyst"
                            onChange={event =>
                              this.updateResponsabilityPart(
                                event,
                                index,
                                "respFunction"
                              )
                            }
                            className={classnames(
                              "form-control form-control-lg",
                              {
                                "is-invalid": errors.reponsibility
                              }
                            )}
                          />
                        </div>
                      </div>
                      <div className="form-group col">
                        <div className="col-6 text-right">
                          <FormControl
                            placeholder="Exc.: Write technical analysis for..."
                            onChange={event =>
                              this.updateResponsabilityPart(
                                event,
                                index,
                                "respTask"
                              )
                            }
                            className={classnames(
                              "form-control form-control-lg",
                              {
                                "is-invalid": errors.reponsibility
                              }
                            )}
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
              <div className="text-center">
                {errors.reponsibility && (
                  <div
                    className="alert alert-danger"
                    role="alert"
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {errors.reponsibility}
                  </div>
                )}
              </div>
            </div>
          </Form>
        );
      }
    };

    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/editcv/${this.props.profileIdentifier}`}
                className="btn btn-light"
              >
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">
                {typeForm} Client/Project
              </h4>
              <div className="lead text-center">
                <hr />
                {this.props.profileSequence && (
                  <span>Client/Project ID: {this.props.profileSequence} </span>
                )}
                {errors.profileNotFound && (
                  <div
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {"Please Check the fields above:"}
                    <hr />
                    {errors.profileNotFound}
                  </div>
                )}
              </div>
              <form onSubmit={this.onSubmit}>
                <div className="container">
                  <div className="row">
                    <div className="form-group col">
                      <h6>Start Date</h6>
                      <input
                        type="date"
                        className={classnames("form-control form-control-lg", {
                          "is-invalid": errors.datesProject
                        })}
                        name="start_date"
                        value={this.state.start_date}
                        onChange={this.onChange}
                      />
                    </div>
                    <div className="form-group col">
                      <h6>End Date</h6>
                      <input
                        type="date"
                        className={classnames("form-control form-control-lg", {
                          "is-invalid": errors.datesProject
                        })}
                        name="end_date"
                        value={this.state.end_date}
                        onChange={this.onChange}
                      />
                    </div>
                    <div className="col-12 text-center">
                      {errors.datesProject && (
                        <div
                          style={{
                            width: "100%",
                            marginTop: ".25rem",
                            fontSize: "80%",
                            color: "#dc3545"
                          }}
                        >
                          {errors.datesProject}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
                <h6>Position:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.position
                    })}
                    placeholder="Position"
                    name="position"
                    value={this.state.position}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.position && (
                    <div className="invalid-feedback">{errors.position}</div>
                  )}
                </div>
                <h6>Company:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.company
                    })}
                    placeholder="Company"
                    name="company"
                    value={this.state.company}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.company && (
                    <div className="invalid-feedback">{errors.company}</div>
                  )}
                </div>
                <h6>General Context:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.generalContext
                    })}
                    placeholder="General Context"
                    name="generalContext"
                    value={this.state.generalContext}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.generalContext && (
                    <div className="invalid-feedback">
                      {errors.generalContext}
                    </div>
                  )}
                </div>

                <h6>Responsibilities:</h6>
                {boardAlgorithm(errors, this.state.responsibilities, typeForm)}
                <hr />

                <Button
                  caption="Add Responsability"
                  cssClass="btn btn-light"
                  onClick={() => this.addResponsability()}
                >
                  Add Responsability
                </Button>
                <hr />
                <h6>Technical Enviroment:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.technicalEnviroment
                    })}
                    placeholder="Technical Enviroment"
                    name="technicalEnviroment"
                    value={this.state.technicalEnviroment}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                    style={{
                      marginTop: "0px",
                      marginBottom: "0px",
                      height: "100px"
                    }}
                  />
                  {errors.technicalEnviroment && (
                    <div className="invalid-feedback">
                      {errors.technicalEnviroment}
                    </div>
                  )}
                </div>
                <input
                  type="submit"
                  value="Save"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ClientProjectForm.propTypes = {
  //getProfileTask: PropTypes.func.isRequired,
  addProfileTask: PropTypes.func.isRequired,
  updateProfileTask: PropTypes.func.isRequired,
  title: PropTypes.object.isRequired,
  profile_task: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  title: state.title,
  profile_task: state.profileBacklog.profile_task,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { addProfileTask, updateProfileTask }
)(ClientProjectForm);
