import React, { Component } from "react";
import { Link } from "react-router-dom";
import { deleteProfileTask } from "../../../actions/profileBacklogActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class ProfileTask extends Component {
  onDeleteClick(backlog_id, pt_id) {
    this.props.deleteProfileTask(backlog_id, pt_id);
  }
  render() {
    const { profile_task } = this.props;
    let priorityString;
    let priorityClass;

    if (profile_task.priority === 1) {
      priorityClass = "bg-danger text-light";
      priorityString = "HIGH";
    }

    if (profile_task.priority === 2) {
      priorityClass = "bg-warning text-light";
      priorityString = "MEDIUM";
    }

    if (profile_task.priority === 3) {
      priorityClass = "bg-info text-light";
      priorityString = "LOW";
    }

    return (
      <div className="card mb-1 bg-light">
        <div className={`card-header text-primary ${priorityClass}`}>
          ID: {profile_task.projectSequence} -- Priority: {priorityString}
        </div>
        <div className="card-body bg-light">
          <h5 className="card-title">{profile_task.summary}</h5>
          <p className="card-text text-truncate ">
            {profile_task.acceptanceCriteria}
          </p>
          <Link
            to={`/ProfileTask/${profile_task.projectIdentifier}/${
              profile_task.projectSequence
            }`}
            className="btn btn-primary"
          >
            View / Update
          </Link>

          <button
            className="btn btn-danger ml-4"
            onClick={this.onDeleteClick.bind(
              this,
              profile_task.projectIdentifier,
              profile_task.projectSequence
            )}
          >
            Delete
          </button>
        </div>
      </div>
    );
  }
}

ProfileTask.propTypes = {
  deleteProfileTask: PropTypes.func.isRequired
};
export default connect(
  null,
  { deleteProfileTask }
)(ProfileTask);
