import axios from "axios";
import { GET_ERRORS, GET_PROFILES, GET_PROFILE, DELETE_PROFILE } from "./types";
import { setJWTTokenCandidate } from "../securityUtils/setJWTToken";
import jwt_decode from "jwt-decode";

export const createProfile = (profile, history) => async dispatch => {
  try {
    await axios.post("/api/profile", profile);
    history.push("/profiles");
    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const getProfiles = () => async dispatch => {
  const res = await axios.get("/api/profile/all");
  dispatch({
    type: GET_PROFILES,
    payload: res.data
  });
};

export const getProfile = (id, history) => async dispatch => {
  try {
    const res = await axios.get(`/api/profile/${id}`);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    history.push("/profiles");
  }
};

export const getProfileByLink = (linkCv, token, history) => async dispatch => {
  try {
    //const res = await axios.post("/api/users/editcv", linkCv);
    //const res = await axios.post("/api/profile/editcv", linkCv);
    const res = await axios.get(`/api/profile/editcv/${linkCv}`);

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (error) {
    console.log(error);
    //history.push("/profiles");
  }
};

export const updateCV = profile => async dispatch => {
  console.log("updateCV", profile);
  try {
    const res = await axios.post("/api/editcv", profile);

    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });

    dispatch({
      type: GET_ERRORS,
      payload: {}
    });
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data
    });
  }
};

export const deleteProfile = id => async dispatch => {
  if (
    window.confirm(
      "Are you sure? This will delete the project and all the data related to it"
    )
  ) {
    await axios.delete(`/api/profile/${id}`);
    dispatch({
      type: DELETE_PROFILE,
      payload: id
    });
  }
};
