import React, { Component } from "react";
import { getProfileByLink, updateCV } from "../../actions/profileActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import requireAuth from "../hocs/requireAuth";
import { setJWTTokenCandidate } from "../../securityUtils/setJWTToken";
import jwt_decode from "jwt-decode";
import { getProfileBacklog } from "../../actions/profileBacklogActions";
import CVBacklog from "./CVBacklog";
import jsscompress from "js-string-compression";
import { TASK_TYPE_SKILSS, TASK_TYPE_EDUC } from "../../actions/types";

class CVBoard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      fullName: "",
      candidateEmail: "",
      profileIdentifier: "",
      token: "",
      summary: "",
      technologyArea: 0,
      domains: "",
      certifications: "",
      tools: "",
      trainings: "",
      methodologies: "",
      others: "",
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    const { id } = this.props.match.params;

    this.props.getProfileByLink(id, this.props.history);

    this.props.getProfileBacklog(id, this.props.history);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    const {
      id,
      fullName,
      candidateEmail,
      profileIdentifier,
      token,
      summary,
      technologyArea,
      domains,
      certifications,
      tools,
      trainings,
      methodologies,
      others
    } = nextProps.profile;

    const jwtToken = localStorage.jwtToken;
    // Only Take Care About Candidate Toke If doenst exist Current User
    if (typeof jwtToken === "undefined" && typeof token != "undefined") {
      // set our token in header ***
      setJWTTokenCandidate(token);

      // store the token in the localStorage
      localStorage.setItem("jwtTokenCandidate", token);
      // decode token on React
      const decoded_jwtToken = jwt_decode(token);
      console.log(
        "Expire Remaining:",
        decoded_jwtToken.exp - Date.now() / 1000
      );

      const currentTime = Date.now() / 1000;
      if (decoded_jwtToken.exp < currentTime) {
        window.location.href = "/";
      }
    }

    this.setState({
      id,
      fullName,
      candidateEmail,
      profileIdentifier,
      token,
      technologyArea,
      domains,
      certifications,
      tools,
      trainings,
      methodologies,
      others
    });

    if (summary) {
      var hm = new jsscompress.Hauffman();
      var decompressed = hm.decompress(summary);
      this.setState({ summary: decompressed });
      console.log("Decompresed", hm.decompress(summary));
    }
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    const updateProfile = {
      id: this.state.id,
      fullName: this.state.fullName,
      candidateEmail: this.state.candidateEmail,
      profileIdentifier: this.state.profileIdentifier,
      summary: this.state.summary,
      technologyArea: this.state.technologyArea,
      domains: this.state.domains,
      certifications: this.state.certifications,
      tools: this.state.tools,
      trainings: this.state.trainings,
      methodologies: this.state.methodologies,
      others: this.state.others
    };

    console.log(updateProfile);

    this.props.updateCV(updateProfile, this.props.history);
  }

  render() {
    const { errors } = this.state;
    const { id } = this.props.match.params;
    const { profile_tasks } = this.props.profileBacklog;

    const boardAlgorithm = (errors, profile_tasks, taskType_To_Filter) => {
      if (profile_tasks.length < 1) {
        //PROJECT IDENTIFIER CHECK
        if (errors.profileNotFound) {
          return (
            <div className="alert alert-danger text-center" role="alert">
              {errors.projectNotFound}
            </div>
          );
        } else if (errors.profileIdentifier) {
          return (
            <div className="alert alert-danger text-center" role="alert">
              {errors.profileIdentifier}
            </div>
          );
        } else {
          return { task_types_to_render };
        }
      } else {
        return (
          <CVBacklog
            profile_tasks_prop={profile_tasks}
            task_type_prop={taskType_To_Filter}
          />
        );
      }
    };

    const task_types_to_render = taskType => {
      switch (taskType) {
        case TASK_TYPE_SKILSS:
          return (
            <div className="alert alert-info text-center" role="alert">
              Skills
            </div>
          );

        case TASK_TYPE_EDUC:
          return (
            <div className="alert alert-info text-center" role="alert">
              Educations
            </div>
          );

        default:
          return (
            <div className="alert alert-info text-center" role="alert">
              Hi There
            </div>
          );
      }
    };

    return (
      <div className="profile">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link to="/profiles" className="btn btn-light">
                Back to Profiles
              </Link>
              <div className="display-4 text-center">Edit CV form</div>
              <hr />
              <div className="text-center">Session expire in 2 days</div>
              <hr />
              <form onSubmit={this.onSubmit}>
                <h6>Full name :</h6>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.fullName
                    })}
                    placeholder="Full Name"
                    name="fullName"
                    onChange={this.onChange}
                    value={this.state.fullName}
                    autoFocus={true}
                  />
                  {errors.fullName && (
                    <div className="invalid-feedback">{errors.fullName}</div>
                  )}
                </div>
                <div className="form-group">
                  <h6>Technology Area :</h6>
                  <select
                    className="form-control form-control-lg"
                    name="technologyArea"
                    value={this.state.technologyArea}
                    onChange={this.onChange}
                  >
                    <option value="">Technology Area</option>
                    <option value="Software">Software</option>
                    <option value="Engineer">Engineer</option>
                  </select>
                </div>
                <hr />
                <h6>Experiences:</h6>
                <div className="form-group">
                  <textarea
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.summary
                    })}
                    placeholder="Summary of profile"
                    name="summary"
                    value={this.state.summary}
                    onChange={this.onChange}
                  />
                  {errors.summary && (
                    <div className="invalid-feedback">{errors.summary}</div>
                  )}
                </div>
                <h6>Years of Experiences:</h6>
                <div className="card">
                  <div className="card-body">
                    <div className="col-12 text-right">
                      <Link
                        to={`/addProfileTask/${id}/${"skills"}`}
                        className="btn btn-primary mb-3"
                      >
                        <i className="fas fa-plus-circle"> Add Skills</i>
                      </Link>
                    </div>
                    {boardAlgorithm(errors, profile_tasks, "skill")}
                  </div>
                </div>
                <hr />
                <h6>Domains:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.domains
                    })}
                    placeholder="Domains"
                    name="domains"
                    value={this.state.domains}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.domains && (
                    <div className="invalid-feedback">{errors.domains}</div>
                  )}
                </div>
                <h6>Certifications:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.certifications
                    })}
                    placeholder="Certifications"
                    name="certifications"
                    value={this.state.certifications}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.certifications && (
                    <div className="invalid-feedback">
                      {errors.certifications}
                    </div>
                  )}
                </div>
                <h6>Tools:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.tools
                    })}
                    placeholder="Tools"
                    name="tools"
                    value={this.state.tools}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.tools && (
                    <div className="invalid-feedback">{errors.tools}</div>
                  )}
                </div>
                <h6>Trainings:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.trainings
                    })}
                    placeholder="Trainings"
                    name="trainings"
                    value={this.state.trainings}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.trainings && (
                    <div className="invalid-feedback">{errors.trainings}</div>
                  )}
                </div>
                <h6>Methodologies:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.methodologies
                    })}
                    placeholder="Methodologies"
                    name="methodologies"
                    value={this.state.methodologies}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.methodologies && (
                    <div className="invalid-feedback">
                      {errors.methodologies}
                    </div>
                  )}
                </div>
                <h6>Others:</h6>
                <div className="form-group">
                  <textarea
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.others
                    })}
                    placeholder="Others"
                    name="methodologies"
                    value={this.state.others}
                    // onChange={this.onChange.bind(this)}
                    onChange={this.onChange}
                  />
                  {errors.others && (
                    <div className="invalid-feedback">{errors.others}</div>
                  )}
                </div>
                <hr />
                <h6>Education - Languages:</h6>
                <div className="card">
                  <div className="card-body">
                    <div className="col-12 text-right">
                      <Link
                        to={`/addProfileTask/${id}/${"educ"}`}
                        className="btn btn-primary mb-3"
                      >
                        <i className="fas fa-plus-circle"> Add Educations</i>
                      </Link>
                    </div>
                    {boardAlgorithm(errors, profile_tasks, "educ")}
                  </div>
                </div>
                <hr />

                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

CVBoard.propTypes = {
  profileBacklog: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  getProfileByLink: PropTypes.func.isRequired,
  updateCV: PropTypes.func.isRequired,
  getProfileBacklog: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile.profile,
  profileBacklog: state.profileBacklog,
  errors: state.errors,
  //profileBacklog: state.profile.profileBacklog,
  getProfileByLink: state.getProfileByLink,
  getProfileBacklog: state.getProfileBacklog
});

export default connect(
  mapStateToProps,
  { getProfileByLink, updateCV, getProfileBacklog }
)(requireAuth(CVBoard));
