import React, { Component } from "react";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import {
  getProfileTask,
  updateProfileTask
} from "../../../actions/profileBacklogActions";
import PropTypes from "prop-types";
import NumericInput from "react-numeric-input";

class UpdateProfileTask extends Component {
  constructor() {
    super();

    this.state = {
      id: "",
      profileSequence: "",
      summary: "",
      acceptanceCriteria: "",
      taskType: "",
      totalYears: "",
      priority: "",
      dueDate: "",
      profileIdentifier: "",
      create_At: "",
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onChangeText = this.onChangeText.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    const { profile_backlog_id, pt_id } = this.props.match.params;
    this.props.getProfileTask(profile_backlog_id, pt_id, this.props.history);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    const {
      id,
      profileSequence,
      summary,
      acceptanceCriteria,
      taskType,
      totalYears,
      priority,
      dueDate,
      profileIdentifier,
      create_At
    } = nextProps.profile_task;

    this.setState({
      id,
      profileSequence,
      summary,
      acceptanceCriteria,
      taskType,
      totalYears,
      priority,
      dueDate,
      profileIdentifier,
      create_At
    });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeText(value) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ totalYears: e });
    // }
    if (value) {
      if (!/^[0-9]+$/.test(value.toString())) {
        this.setState({ totalYears: "" });
      } else {
        this.setState({ totalYears: value.toString() });
      }
    }
  }

  onSubmit(e) {
    e.preventDefault();

    const UpdateProfileTask = {
      id: this.state.id,
      profileSequence: this.state.profileSequence,
      summary: this.state.summary,
      acceptanceCriteria: this.state.acceptanceCriteria,
      taskType: this.state.taskType,
      totalYears: this.state.totalYears,
      priority: this.state.priority,
      dueDate: this.state.dueDate,
      profileIdentifier: this.state.profileIdentifier,
      create_At: this.state.create_At
    };

    // console.log(UpdateProfileTask);
    this.props.updateProfileTask(
      this.state.profileIdentifier,
      this.state.profileSequence,
      UpdateProfileTask,
      this.props.history
    );
  }

  render() {
    const { errors } = this.state;

    const taskAlgorithm = () => {
      if (this.state.taskType === "skill") {
        return "Skill";
      } else if (this.state.taskType === "educ") {
        return "Education";
      }
    };

    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/editcv/${this.state.profileIdentifier}`}
                className="btn btn-light"
              >
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">
                Update {taskAlgorithm()}
              </h4>
              <div className="lead text-center">
                Profile Link: {this.state.profileIdentifier}
                <hr />
                {taskAlgorithm()} ID: {this.state.profileSequence}{" "}
              </div>
              <form onSubmit={this.onSubmit}>
                <h6>Competences:</h6>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.summary
                    })}
                    name="summary"
                    placeholder="Profile Task summary"
                    value={this.state.summary}
                    onChange={this.onChange}
                  />
                  {errors.summary && (
                    <div className="invalid-feedback">{errors.summary}</div>
                  )}
                </div>
                <h6>Total of Years:</h6>
                {/* <NumericInput
                  value={this.state.value}
                  onChange={value => this.setState({ value })}
                  onLimitReached={(isMax, msg) => console.log(isMax, msg)}
                  totalWidth={240}
                  totalHeight={50}
                  iconSize={25}
                  step={1.5}
                  valueType="real"
                  rounded
                  textColor="#B0228C"
                  iconStyle={{ color: "white" }}
                  rightButtonBackgroundColor="#EA3788"
                  leftButtonBackgroundColor="#E56B70"
                /> */}
                <NumericInput
                  totalWidth={240}
                  totalHeight={50}
                  mobile
                  valueType="real"
                  min={0}
                  max={50}
                  className="form-control"
                  name="totalYears"
                  //onChange={value => this.setState({ value })}
                  value={this.state.totalYears}
                  onLimitReached={(isMax, msg) => console.log(isMax, msg)}
                />
                {errors.totalYears && (
                  <div
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {errors.totalYears}
                  </div>
                )}
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

UpdateProfileTask.propTypes = {
  getProfileTask: PropTypes.func.isRequired,
  profile_task: PropTypes.object.isRequired,
  updateProfileTask: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile_task: state.profileBacklog.profile_task,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { getProfileTask, updateProfileTask }
)(UpdateProfileTask);
