import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { deleteProfile } from "../../actions/profileActions";

class ProfileItem extends Component {
  onDeleteClick = id => {
    this.props.deleteProfile(id);
  };

  render() {
    const { profile } = this.props;
    return (
      <div className="container">
        <div className="card card-body bg-light mb-3">
          <div className="row">
            <div className="col-2">
              <span className="mx-auto">{profile.fullName}</span>
            </div>
            <div className="col-lg-6 col-md-4 col-8">
              <h3>{profile.fullName}</h3>
              <p>{profile.candidateEmail}</p>
            </div>
            <div>
              <div className="collapse navbar-collapse" id="mobile-nav">
                <ul className="navbar-nav mr-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/profiles">
                      Profiles CV
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/dashboard">
                      Dashboard
                    </Link>
                  </li>
                </ul>
                <ul className="navbar-nav ml-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/dashboard">
                      <i className="fas fa-user-circle mr-1" />
                      {user.fullName}
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link
                      className="nav-link"
                      to="/logout"
                      onClick={this.logout.bind(this)}
                    >
                      Logout
                    </Link>
                  </li>
                </ul>
              </div>
              <ul className="list-group">
                <Link
                  to={`/editcv/${profile.profileIdentifier}`}
                  style={
                    profile.profileIdentifier ? null : { pointerEvents: "none" }
                  }
                >
                  <li className="list-group-item board">
                    <i
                      className="fa fa-flag-checkered pr-1"
                      style={
                        profile.profileIdentifier ? null : { color: "#dddddd" }
                      }
                    >
                      {" "}
                      Open CV
                    </i>
                  </li>
                </Link>
                <Link to={`/updateProfile/${profile.candidateEmail}`}>
                  <li className="list-group-item update">
                    <i className="fa fa-edit pr-1"> Token</i>
                  </li>
                </Link>

                <li
                  className="list-group-item delete"
                  onClick={this.onDeleteClick.bind(
                    this,
                    profile.candidateEmail
                  )}
                >
                  <i className="fa fa-minus-circle pr-1"> Delet</i>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ProfileItem.propTypes = {
  deleteProfile: PropTypes.func.isRequired
};

export default connect(
  null,
  { deleteProfile }
)(ProfileItem);
