import React, { Component } from "react";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import NumericInput from "react-numeric-input";
import { updateProfileTask } from "../../../../actions/profileBacklogActions";

class EducationForm extends Component {
  constructor() {
    super();

    this.state = {
      id: "",
      profileSequence: "",
      profileIdentifier: "",
      summary: "",
      educYear: "",
      educAwarded: "",
      educOrganization: "",
      create_At: "",
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onChangeText = this.onChangeText.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    console.log(this.state);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    const {
      id,
      profileSequence,
      profileIdentifier,
      summary,
      educYear,
      educAwarded,
      educOrganization,
      create_At
    } = nextProps.profile_task;

    this.setState({
      id,
      profileSequence,
      profileIdentifier,
      summary,
      educYear,
      educAwarded,
      educOrganization,
      create_At
    });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeText(value) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ educYear: e });
    // }
    if (value) {
      if (!/^[0-9]+$/.test(value.toString())) {
        this.setState({ educYear: "" });
      } else {
        this.setState({ educYear: value.toString() });
      }
    }
  }

  onSubmit(e) {
    e.preventDefault();

    const UpdateProfileTask = {
      id: this.state.id,
      profileSequence: this.state.profileSequence,
      profileIdentifier: this.state.profileIdentifier,
      educYear: this.state.educYear,
      educAwarded: this.state.educAwarded,
      educOrganization: this.state.educOrganization,
      create_At: this.state.create_At
    };

    // console.log(UpdateProfileTask);
    this.props.updateProfileTask(
      this.state.profileIdentifier,
      this.state.profileSequence,
      UpdateProfileTask,
      this.props.history
    );
  }

  render() {
    const { errors } = this.props;

    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/editcv/${this.state.profileIdentifier}`}
                className="btn btn-light"
              >
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">Update Education</h4>
              <div className="lead text-center">
                <hr />
                Education ID: {this.state.profileSequence}{" "}
              </div>
              <form onSubmit={this.onSubmit}>
                <h6>Organization:</h6>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.educOrganization
                    })}
                    name="educOrganization"
                    placeholder="Organization"
                    value={this.state.educOrganization}
                    onChange={this.onChange}
                  />
                  {errors.educOrganization && (
                    <div className="invalid-feedback">
                      {errors.educOrganization}
                    </div>
                  )}
                </div>
                <h6>Year:</h6>
                <NumericInput
                  value={this.state.value}
                  onChange={value => this.setState({ value })}
                  onLimitReached={(isMax, msg) => console.log(isMax, msg)}
                  totalWidth={240}
                  totalHeight={50}
                  iconSize={25}
                  step={1}
                  valueType="real"
                  min={1900}
                  max={new Date().getFullYear()}
                  rounded
                  textColor="#B0228C"
                  iconStyle={{ color: "white" }}
                  rightButtonBackgroundColor="#EA3788"
                  leftButtonBackgroundColor="#E56B70"
                />
                {errors.educYear && (
                  <div
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {errors.educYear}
                  </div>
                )}
                <div className="form-group">
                  <h6>Qualification Awarded :</h6>
                  <select
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.educAwarded
                    })}
                    name="educAwarded"
                    value={this.state.educAwarded}
                    onChange={this.onChange}
                  >
                    <option value="">Awarded</option>
                    <option value="Diplom">Diplom</option>
                    <option value="Bsc">Bsc</option>
                    <option value="Msc">Msc</option>
                    <option value="Mag">Mag</option>
                    <option value="Dipl.Ing">Dipl.Ing</option>
                    <option value="Phd">Phd</option>
                    <option value="Dr">Dr</option>
                  </select>
                  {errors.educAwarded && (
                    <div
                      style={{
                        width: "100%",
                        marginTop: ".25rem",
                        fontSize: "80%",
                        color: "#dc3545"
                      }}
                    >
                      {errors.educAwarded}
                    </div>
                  )}
                </div>
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

EducationForm.propTypes = {
  profile_task: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile_task: state.profileBacklog.profile_task,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { updateProfileTask }
)(EducationForm);
