import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getProfileTask,
  updateProfileTask
} from "../../../actions/profileBacklogActions";
import PropTypes from "prop-types";
import EducationForm from "./TasksForms/EducationForm";
import SkillsForm from "./TasksForms/SkillsForm";
import LanguageForm from "./TasksForms/LanguageForm";
import ClientProjectForm from "./TasksForms/ClientProjectForm";

class UpdateProfileTask extends Component {
  constructor(props) {
    super(props);
    const { profile_backlog_id, pt_id, taskType } = this.props.match.params;

    this.state = {
      taskType: taskType,
      priority: 0,
      profileIdentifier: profile_backlog_id,
      profileSequence: pt_id,
      totalYears: 0,
      profile_task: {},
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  // componentDidMount() {
  //   const { profile_backlog_id, pt_id } = this.props.match.params;
  //   this.props.getProfileTask(profile_backlog_id, pt_id, this.props.history);
  // }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    if (nextProps.profile_task) {
      this.setState({ profile_task: nextProps.profile_task });
    }

    // const {
    //   id,
    //   summary,
    //   acceptanceCriteria,
    //   taskType,
    //   totalYears,
    //   priority,
    //   dueDate,
    //   profileIdentifier,
    //   profileSequence,
    //   create_At
    // } = nextProps.profile_task;

    // this.setState({
    //   id,
    //   summary,
    //   acceptanceCriteria,
    //   taskType,
    //   totalYears,
    //   priority,
    //   dueDate,
    //   profileIdentifier,
    //   profileSequence,
    //   create_At
    // });
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeText(value) {
    // if (/^\d+$/.test(e.toString())) {
    //   this.setState({ totalYears: e });
    // }
    if (value) {
      if (!/^[0-9]+$/.test(value.toString())) {
        this.setState({ totalYears: "" });
      } else {
        this.setState({ totalYears: value.toString() });
      }
    }
  }

  onSubmit(e) {
    e.preventDefault();

    // const UpdateProfileTask = {
    //   id: this.state.id,
    //   profileSequence: this.state.profileSequence,
    //   summary: this.state.summary,
    //   acceptanceCriteria: this.state.acceptanceCriteria,
    //   taskType: this.state.taskType,
    //   totalYears: this.state.totalYears,
    //   priority: this.state.priority,
    //   dueDate: this.state.dueDate,
    //   profileIdentifier: this.state.profileIdentifier,
    //   create_At: this.state.create_At
    // };

    // // console.log(UpdateProfileTask);
    // this.props.updateProfileTask(
    //   this.state.profileIdentifier,
    //   this.state.profileSequence,
    //   UpdateProfileTask,
    //   this.props.history
    // );
  }

  render() {
    const { profile_backlog_id, pt_id, taskType } = this.props.match.params;
    //const { errors } = this.state;

    const taskComponent = taskType => {
      if (taskType === "skill") {
        return (
          <SkillsForm
            typeForm="Update"
            taskType={taskType}
            history={this.props.history}
            profileIdentifier={profile_backlog_id}
            profileSequence={pt_id}
          />
        );
      } else if (taskType === "educ") {
        return (
          <EducationForm
            typeForm="Update"
            taskType={taskType}
            history={this.props.history}
            profileIdentifier={profile_backlog_id}
            profileSequence={pt_id}
          />
        );
      } else if (taskType === "lang") {
        return (
          <LanguageForm
            typeForm="Update"
            taskType={taskType}
            history={this.props.history}
            profileIdentifier={profile_backlog_id}
            profileSequence={pt_id}
          />
        );
      } else if (taskType === "cliPrj") {
        return (
          <ClientProjectForm
            typeForm="Update"
            taskType={taskType}
            history={this.props.history}
            profileIdentifier={profile_backlog_id}
            profileSequence={pt_id}
          />
        );
      }
    };

    return <div>{taskComponent(taskType)}</div>;
  }
}

UpdateProfileTask.propTypes = {
  getProfileTask: PropTypes.func.isRequired,
  profile_task: PropTypes.object.isRequired,
  updateProfileTask: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile_task: state.profileBacklog.profile_task,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { getProfileTask, updateProfileTask }
)(UpdateProfileTask);
