import React, { Component } from "react";
import { connect } from "react-redux";
import classnames from "classnames";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import NumericInput from "react-numeric-input";

class EducationForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      profileSequence: "",
      summary: "",
      acceptanceCriteria: "",
      taskType: "",
      totalYears: "",
      priority: "",
      dueDate: "",
      profileIdentifier: "",
      create_At: "",
      errors: {}
    };
    //this.onChange = this.onChange.bind(this);
    //this.onChangeText = this.onChangeText.bind(this);
    //this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    console.log(this.state);
    const { profile_task } = this.state;
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }

    const {
      id,
      profileSequence,
      summary,
      acceptanceCriteria,
      taskType,
      totalYears,
      priority,
      dueDate,
      profileIdentifier,
      create_At
    } = nextProps.profile_task;

    this.setState({
      id,
      profileSequence,
      summary,
      acceptanceCriteria,
      taskType,
      totalYears,
      priority,
      dueDate,
      profileIdentifier,
      create_At
    });
  }

  render() {
    const { errors } = this.props;

    return (
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/editcv/${this.props.profileIdentifier}`}
                className="btn btn-light"
              >
                Back to Edit CV
              </Link>
              <h4 className="display-4 text-center">Update Education</h4>
              <div className="lead text-center">
                <hr />
                Education ID: {this.props.profileSequence}{" "}
              </div>
              <form onSubmit={this.onSubmit}>
                <h6>Organization:</h6>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg", {
                      "is-invalid": errors.summary
                    })}
                    name="summary"
                    placeholder="Profile Task summary"
                    value={this.props.summary}
                    onChange={this.onChange}
                  />
                  {errors.summary && (
                    <div className="invalid-feedback">{errors.summary}</div>
                  )}
                </div>
                <h6>Year:</h6>
                <NumericInput
                  value={this.props.value}
                  onChange={value => this.setState({ value })}
                  onLimitReached={(isMax, msg) => console.log(isMax, msg)}
                  totalWidth={240}
                  totalHeight={50}
                  iconSize={25}
                  step={1}
                  valueType="real"
                  min={1900}
                  max={new Date().getFullYear()}
                  rounded
                  textColor="#B0228C"
                  iconStyle={{ color: "white" }}
                  rightButtonBackgroundColor="#EA3788"
                  leftButtonBackgroundColor="#E56B70"
                />
                {errors.totalYears && (
                  <div
                    style={{
                      width: "100%",
                      marginTop: ".25rem",
                      fontSize: "80%",
                      color: "#dc3545"
                    }}
                  >
                    {errors.totalYears}
                  </div>
                )}
                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

EducationForm.propTypes = {
  profile_task: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile_task: state.profileBacklog.profile_task,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  {}
)(EducationForm);
